glsl3.vert:
precision highp float;
uniform CCGlobal {
  vec4 cc_time;

  vec4 cc_screenSize;

  vec4 cc_screenScale;

  vec4 cc_nativeSize;

  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;

  vec4 cc_exposure;

  vec4 cc_mainLitDir;

  vec4 cc_mainLitColor;

  vec4 cc_ambientSky;
  vec4 cc_ambientGround;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};

in vec3 a_position;
in vec2 a_uv0;
out vec2 v_uv0;
in vec4 a_color;
out vec4 v_color;
void main () {
  gl_Position = cc_matViewProj * vec4(a_position, 1);
  v_uv0 = a_uv0;v_color = a_color;
}

glsl3.frag:
precision highp float;

uniform sampler2D texture;
in vec4 v_color;
in vec2 v_uv0;
uniform Replace{
 vec4 referColor;
 vec4 rangeColor;
 vec4 replaceColor;
}
vec4 color_replace(vec4 color)
{
  if(referColor.r <= color.r && color.r < (referColor.r + rangeColor.r)){
    if(referColor.g <= color.g && color.g < (referColor.g + rangeColor.g)){
      if(referColor.b <= color.b && color.b < (referColor.b + rangeColor.b)){
      if(referColor.a <= color.a && color.a < (referColor.a + rangeColor.a)){
        color = vec4(replaceColor.rgb,color.a);
      }
      }
    }
  }
  return color;
}

void main () {
  vec4 color = texture2D(texture, v_uv0);
  color*=v_color;
  gl_FragColor = color_replace(color);
}

